select t.putontime as putontime,t.tname,t.id as id,t.tinterest*100 as tinterest,t.loantime,t.dayormonth,t.tamount, t.finishtamount,t.tbegintime,t.tstatus from TENDERITEM t where t.displaytype=1 and t.onanddown =1
and t.dayormonth='月' and t.loantime in
#{loant}
and t.repaymentpro in
#{tpro}
and t.tstatus in
#{tatu}
union all select t1.putontime as putontime,t1.tname,t1.id as id,t1.tinterest*100 as tinterest,t1.loantime,t1.dayormonth,t1.tamount, t1.finishtamount,t1.tbegintime as tbegintime,t1.tstatus from tenderitem t1 where t1.displaytype=1 and t1.onanddown =1 and t1.dayormonth='年'
and t1.repaymentpro in
#{tpro}
and t1.tstatus in
#{tatu}
union all select t2.putontime as putontime,t2.tname,t2.id as id,t2.tinterest*100 as tinterest,t2.loantime,t2.dayormonth,t2.tamount, t2.finishtamount,t2.tbegintime as tbegintime,t2.tstatus from tenderitem t2 where t2.displaytype=1 and t2.onanddown =1 and t2.dayormonth='天'
and t2.repaymentpro in
#{tpro}
and t2.tstatus in
#{tatu}

