select t.* from TENDERITEM t where tstatus =1 or tstatus=9

    id=#{id,jdbcType=DECIMAL}


    and tname = #{tname,jdbcType=VARCHAR}


    and TNO = #{tno,jdbcType=VARCHAR}

select t.* from TENDERITEM t

    
        id=#{id,jdbcType=DECIMAL}
    
    
        and TNO = #{tno,jdbcType=VARCHAR}
    
    
        and TNAME = #{tname,jdbcType=VARCHAR}
    
    
        and TSTATUS = #{tstatus,jdbcType=DECIMAL}

        select
        
        from TENDERITEM t left join loanapp t2 on t.loanappid = t2.id
        
            
                and t2.baseid = #{baseid,jdbcType=DECIMAL}
            
            
                and t.tstatus = #{tstatus,jdbcType=DECIMAL}
